    private void search_ButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_search_ButtonActionPerformed
        //search
        String keyword = searchKeyword_TextField.getText();
        
        int port = 5000;
        String group = "225.4.5.6";
        int ttl = 1;
        
        try{
            MulticastSocket s = new MulticastSocket();
        
            //byte buf[] = new byte[10];
            byte buf[] = keyword.getBytes();
            //for (int i=0; i<buf.length; i++) buf[i] = (byte)i;
            DatagramPacket pack = new DatagramPacket(buf, buf.length,
					 InetAddress.getByName(group), port);
            s.send(pack,(byte)ttl);
            s.close();
            
        } catch(Exception e){
            System.err.println(e);
        }
        
        
        
        
    }//GEN-LAST:event_search_ButtonActionPerformed

    private void connect_ButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_connect_ButtonActionPerformed
        //connect
        String multicast_ip = ip_TextField.getText();
        String multicast_port_string = port_TextField.getText();
        int multicast_port = Integer.parseInt(multicast_port_string);
        
        multicast_listen ml = new multicast_listen(multicast_ip,multicast_port);
        ml.start();
        
        
    }//GEN-LAST:event_connect_ButtonActionPerformed
    
