    class multicast_listen extends Thread {
        
        String mc_ip = "225.4.5.6";
        int mc_port = 5000;
        
        multicast_listen(String ip,int port){
            mc_ip = ip;
            mc_port = port;
        }
        
        public void run() {
            
            try {
             while(true){
                MulticastSocket s = new MulticastSocket(mc_port);
                s.joinGroup(InetAddress.getByName(mc_ip));
                byte buf[] = new byte[1024];
                DatagramPacket pack = new DatagramPacket(buf, buf.length);
                s.receive(pack);
                
                System.out.println("Received data from: " + pack.getAddress().toString() +
		    ":" + pack.getPort() + " with length: " +
		    pack.getLength());
                
                log_TextArea.append("Received data from: " + pack.getAddress().toString() +
		    ":" + pack.getPort() + " with length: " +
		    pack.getLength()+"\n");
                
                
                
                byte[] bytearray = pack.getData();
                String sss="";
                for(int i=0; i<pack.getLength(); i++){
                    sss+=(char)bytearray[i];
                }
                
                log_TextArea.append(sss +"\n");
                
                
                System.out.write(pack.getData(),0,pack.getLength());
                System.out.println();
                
                //s.leaveGroup(InetAddress.getByName(mc_ip));
                //s.close();
             }

                
            } catch(Exception e){
                System.err.println(e);
            }
            
            
        }
    }
    
    
    
